const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if(formState.email == "" || formState.password == "") {
        setFormState((prev) => ({...prev, message: "All fields are mandatory", textStyle: "danger"}));
        return;
    }

    if(!validateEmail(formState.email)) {
        setFormState((prev) => ({...prev, message: "Invalid email", textStyle: "danger"}));
        return;
    }

    setFormState((prev) => ({...prev, message: "Login success", textStyle: "success"}));
}

